mapper.select:
select

from token_usage
where user_id = #{userId}
and is_deleted = 0

    and call_type = #{callType}


    and create_time >= #{startTime}



        and create_time <= #{endTime}


order by create_time desc
limit #{offset}, #{size}

select count(*)
from token_usage
where user_id = #{userId}
and is_deleted = 0

    and call_type = #{callType}


    and create_time >= #{startTime}



        and create_time <= #{endTime}
